mapper.update:
update test
set magic_id = #{magic_id,jdbcType=VARCHAR},
  first_name = #{first_name,jdbcType=VARCHAR},
  last_name = #{last_name,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, magic_id, first_name, last_name
from test
where id = #{id,jdbcType=INTEGER}
